from RSAencrypt import Encrypt
from RSAdecrypt import *
from modDiv import *

def DecipherSmallPrime(ciphertext, modulo, exponent):
    p = 1

    for n in range(2, 1000000):
        if modulo % n == 0:
            p = n
            break
    
    q = modulo // p

    return Decrypt(ciphertext, p, q, exponent)

